import React, { useState } from 'react';
import Header from './components/Header';
import ZoneSelector from './components/ZoneSelector';
import DepartmentList from './components/DepartmentList';
import './App.css';
import useSession from './hooks/useSession';

function App() {
  const [currentZone, setCurrentZone] = useState(null);

  const {
    isSessionActive,
    isExpired,
    remainingTime,
    startSession
  } = useSession();

  const handleSelectZone = (zone) => {
    setCurrentZone(zone);
  };

  const handleGoBack = () => {
    setCurrentZone(null);
  };

  // ðŸ”’ Block entire app when session expired
  if (isExpired) {
    return (
      <div className="App">
        <Header />
        <main className="page-wrapper">
          <section
            style={{
              padding: '40px',
              textAlign: 'center',
              maxWidth: '600px',
              margin: '0 auto'
            }}
          >
            <h2 style={{ color: '#dc2626' }}>Session expired</h2>
            <p>Please scan the QR code again to continue.</p>

            <button
              onClick={startSession}
              style={{
                marginTop: '20px',
                padding: '10px 24px',
                backgroundColor: '#0074D9',
                color: 'white',
                borderRadius: '999px',
                border: 'none',
                cursor: 'pointer'
              }}
            >
              Start new session
            </button>
          </section>
        </main>

        <footer>
          <p className="note-text">Navigation through Mappls App</p>
          &copy; 2025 Ashok Leyland. All rights reserved.
        </footer>
      </div>
    );
  }

  // âœ… Normal flow (session active)
  return (
    <div className="App">
      <Header />

      <main className="page-wrapper">
        {!currentZone ? (
          <ZoneSelector onSelectZone={handleSelectZone} />
        ) : (
          <DepartmentList
            zone={currentZone}
            onBack={handleGoBack}
          />
        )}
      </main>

      <footer>
        <p className="note-text">
          Navigation through Mappls App
          {remainingTime > 0 && (
            <span style={{ marginLeft: '8px', fontSize: '12px', opacity: 0.7 }}>
              (expires in {Math.ceil(remainingTime / 1000)}s)
            </span>
          )}
        </p>
        &copy; 2025 Ashok Leyland. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
