import React, { useState } from 'react';
import Header from './components/Header';
import ZoneSelector from './components/ZoneSelector';
import DepartmentList from './components/DepartmentList';
import './App.css';
import useSession from './hooks/useSession';

function App() {
    const [currentZone, setCurrentZone] = useState(null);

    const { isSessionActive, isExpired, remainingTime, startSession, logout } = useSession();

    const handleSelectZone = (zone) => {
        setCurrentZone(zone);
    };

    const handleGoBack = () => {
        setCurrentZone(null);
    };

    // If session is expired, block access and show minimal UI state to re-start session
    return (
        <div className="App">
            <Header />
            <main className="page-wrapper">
                {!isSessionActive && isExpired ? (
                    <section style={{ padding: '40px', textAlign: 'center', maxWidth: '600px', margin: '0 auto' }}>
                        <h2 style={{ color: '#dc2626' }}>Session expired</h2>
                        <p>Session expired. Please scan QR again.</p>
                        <div style={{ marginTop: '20px' }}>
                            <button
                                onClick={startSession}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: '#0074D9',
                                    color: 'white',
                                    textDecoration: 'none',
                                    borderRadius: '999px',
                                    border: 'none',
                                    cursor: 'pointer'
                                }}
                            >
                                Start new session
                            </button>
                        </div>
                    </section>
                ) : (
                    !currentZone ? (
                        <ZoneSelector onSelectZone={handleSelectZone} />
                    ) : (
                        <DepartmentList zone={currentZone} onBack={handleGoBack} />
                    )
                )}
            </main>

            <footer>
                <p className="note-text">
                    Navigation through Mappls App
                </p>
                &copy; 2025 Ashok Leyland. All rights reserved.
            </footer>
        </div>
    );
}

export default App;
